CREATE TABLE bookissued (issueid int PRIMARY KEY AUTO_INCREMENT, bookid int, uid int, catid int, authorid int, pubid int, FOREIGN KEY (bookid) REFERENCES books (bookid), FOREIGN KEY (uid) REFERENCES members (uid), FOREIGN KEY (catid) REFERENCES category (catid), FOREIGN KEY (authorid) REFERENCES author (authorid), FOREIGN KEY (pubid) REFERENCES publication (pubid), issuedate date not null, toreturndate date not null ) AUTO_INCREMENT=701;