CREATE TABLE books (bookid INT PRIMARY key AUTO_INCREMENT, bookname varchar(200) not null, booknumber varchar(20) not null, catid int, authorid int, pubid int, foreign key (catid) references category (catid), foreign key (authorid) references author(authorid), foreign key (pubid) references publication(pubid), noofcopies int not null, copiesavailable int default noofcopies, ) AUTO_INCREMENT=301;